from indicnlp.tokenize import indic_tokenize #importing the libraries 
def tokenization(indic_string):
    tokens = []
    for t in indic_tokenize.trivial_tokenize(indic_string): #A trivial tokenizer which just tokenizes on the punctuation boundaries. Major punctuations specific to Indian langauges are handled. 
        tokens.append(t)
    return tokens
